     * @param File    file Get the file name and to read the file
     * @param AstFile astFile Get the name of the file
     * @return A file object
     */
    public static File getFileType(Context ctx, File file, AstFile astFile) {
        List<ArticleBase> articles = new ArrayList<>();

        File resultFile = null;
        try {
            String fileName = file.getName();
            switch (FileUtils.getFileEnd(fileName)) {
                case TXT:
                    articles.addAll(convertTxt2Prices(file, astFile));
                    break;
                case DAT:
                    articles.addAll(convertToDAT(file, astFile));
                    break;
                case XLSX:
                    articles.addAll(convertToXLSX(ctx, file, astFile));
                    break;
                default:
                case CSV:
                case NULL:
                    break;
            }

            if (!articles.isEmpty()) {
                ctx.getUserSession().put(Params.ACTIVE_ARTICLES, articles);
            }

        } catch (ConvertException e) {
            log.error("[convertFile] " + e.getLocalizedMessage());
