     *
     * @param File    file Read the file
     * @param AstFile astFile Create a new articlebase object
     * @return A list of articlebase objects
     */
    private static List<ArticleBase> convertToDAT(File file, AstFile astFile) throws ConvertException {
        List<ArticleBase> articles = new ArrayList<>();
        try {
            articles.addAll(convertDat2Prices(file, astFile));
        } catch (IOException e) {
            log.error("[convertFile] " + e.getLocalizedMessage());
        }
        return articles;
