     *
     * @param Context ctx Get the application context
     * @param File    file Get the file path of the excel file
     * @param AstFile astFile Determine if the file is a
     * @return A list of articlebase objects
     */
    private static List<ArticleBase> convertToXLSX(Context ctx, File file, AstFile astFile) throws ConvertException {
        List<ArticleBase> articles = new ArrayList<>();
        try {
            articles.addAll(excelToGeneratePositiv(ctx, file, astFile));
        } catch (Exception e) {
            log.error(e.getLocalizedMessage(), e);
        }
        if (articles.isEmpty()) {
            articles.addAll(excelToPositiv(file));
        }
        return articles;
    }

