package com.hom.ebmw.utilities;

import com.hom.ebmw.utilities.enums.Interval;

public class CronJobUtils {
    /**
     * The create function takes in an Interval and a String value, and returns a String.
     * The function uses the switch statement to determine which case of the interval is being used.
     * Depending on which case is being used, it will format the string accordingly using
     * String.format(). It then returns that formatted string as result.
     *
     * @param Interval interval Determine the interval of time that is being used
     * @param String   value Determine the interval of time
     * @return A cron expression that will trigger the job at a specific time
     */
    public static String create(Interval interval, String value) {
        String result = "";

        switch (interval) {
            case SECOND:
                result = String.format(" %s * * * * ? ", value);
                break;
            case MINUTE:
                result = String.format(" 0 %s * * * ? ", value);
                break;
            case HOUR:
                result = String.format(" 0 0 %s * * ? ", value);
                break;
            default:
            case DAY:
                result = String.format(" 0 0 0 %s * ? ", value);
                break;
            case MONTH:
                result = String.format(" 0 0 0 0 %s ? ", value);
                break;
            case CUSTOM:
                result = "";
                break;
        }

        return result;
    }
}

